import React, { useState, useEffect } from "react";
import "./BlogContanier.css";
import BlogCard from "./BlogCard";

function BlogContainer() {
  const [backendData, setBackendData] = useState([{}]);

  useEffect(() => {
    async function getDataFromBackend() {
      const response = await fetch("/blogs");
      const allBlogs = await response.json();
      setBackendData(allBlogs);
    }
    getDataFromBackend();
  }, []);

  console.log(backendData);
  //   title, author, content,image,role, category, views,likes, date

  return (
    <div class="container BlogContainer">
      {backendData.map((datapt, idx) => {
        <BlogCard
          title={datapt.title}
          author={datapt.author}
          content={datapt.content}
          image={datapt.image}
          role={backendData[idx].role}
          category={backendData[idx].category}
          views={backendData[idx].views}
          likes={backendData[idx].likes}
          date={backendData[idx].date}
        />;
      })}
      {/* <BlogCard  />
      <BlogCard />
      <BlogCard />
      <BlogCard />
      <BlogCard />
      <BlogCard /> */}
    </div>
  );
}

export default BlogContainer;
