import React from "react";
import VisibilityTwoToneIcon from "@mui/icons-material/VisibilityTwoTone";
import FavoriteBorderIcon from "@mui/icons-material/FavoriteBorder";
import TrendingFlatIcon from "@mui/icons-material/TrendingFlat";
import "./BlogCard.css";

const BlogCard = ({
  id,
  title,
  author,
  content,
  image,
  role,
  category,
  views,
  likes,
  date,
}) => {
  //   console.log(key);
  function formatDate(date) {
    const day = date.getDate();
    const month = date.toLocaleString("en-GB", { month: "long" });
    const year = date.getFullYear();
    return `${day} ${month}, ${year}`;
  }

  const dateObject = new Date(date);
  const options = { day: "numeric", month: "long", year: "numeric" };
  const formattedDae = new Intl.DateTimeFormat("en-GB", options).format(
    dateObject
  );
//   const formattedDae = formatDate(dateObject); // Output: "12 July, 2024"

  return (
    <div class="card" style={{ width: "30%", height: "70vh" }}>
      {/* <div class="card" style={{ width: "30%" }}> */}
      <img
        src={image}
        class="card-img-top"
        alt="..."
        height="40%"
        style={{ objectFit: "cover" }}
      />
      <div class="row">
        <div class="card-body" id="#nopadding">
          <p class="card-text">
            <div>
              {date}|<a href="#">{category}</a>
            </div>
          </p>

          <div
            style={{
              height: "20vh",
              overflow: "hidden",
              //   textOverflow: "ellipses",
              //   whiteSpace: "nowrap",
            }}
          >
            <h5 class="card-title">{title}</h5>
            <p
              class="card-text"
              style={{
                height: "20vh",
                overflow: "hidden",
                textOverflow: "ellipsis",
                marginBottom: "16px",
              }}
            >
              {content}
            </p>
          </div>

          <div>
            <a class="readmore btn" href={`/showBlog/${id}`}>
              Read More <TrendingFlatIcon />
            </a>
          </div>

          <div class="container-fluid p-0">
            <div class="row justify-content-between">
              <div class="col-2 p-0">
                <img
                  class="rounded-circle"
                  src="avatar.png"
                  width="47px"
                  alt="..."
                />
              </div>

              <div class="col-10">
                <div class="row justify-content-between">
                  <div class="col-8 p-0">
                    <span>{author}</span>
                  </div>

                  <div class="blogmetadata col-4 p-0">
                    <span>
                      <VisibilityTwoToneIcon />
                      {views}
                    </span>
                    <span>
                      <FavoriteBorderIcon />
                      {likes}
                    </span>
                  </div>
                </div>

                <div class="row">
                  <span class="blogmetadata p-0">{role}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BlogCard;
